    private void btnAvaliarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAvaliarActionPerformed
        double notas = Double.parseDouble(jtNota.getText());
        int frequencia = Integer.parseInt(jtFrequencia.getText());
       
        if (notas >= 7 && frequencia >= 75){
            lblAvaliado.setText("Aprovado ");  
        }else if (notas >= 5 && notas <= 6.9 && frequencia >= 75){
            lblAvaliado.setText("Recuperação");   
        }else if (notas < 5 || frequencia < 75){
            lblAvaliado.setText("Reprovado");
        }
    }//GEN-LAST:event_btnAvaliarActionPerformed

